function get_recipe_list(){var e=new Array;return $.each($("#recipe_list tbody th"),function(t,n){e.push($(n).text())}),e}function drow_recipe_list(e){$("#recipe_list tbody").empty();for(var t in e)$("#recipe_list tbody").append("<tr><th>"+e[t].name+"</th><td>"+e[t].price+'G</td><td><a href="#" onclick="javascript:drop_recipes(this)" class="btn">削除</a></td></tr>')}function add_recipes(){$("#recipes_keyword").attr("disabled","disabled");var e=get_recipe_list();return e.push($("#recipes_keyword").val()),reload_items(e),!1}function drop_recipes(e){return $(e).parents("tr").remove(),reload_items(get_recipe_list()),!1}function reload_items(e){$.getJSON("/recipes.json",{recipes:e},function(e){drow_recipe_list(e.recipe_list),drow_item_list(e.item_list)}),$("#recipes_keyword").val(null),e.length==0?($("#added_contents").hide(),$("#default_contents").show()):($("#added_contents").show(),$("#default_contents").hide()),$("#recipes_keyword").removeAttr("disabled")}function drow_item_list(e){$("#item_list tbody").empty();for(var t in e){var n=e[t].cost;n==0&&(n="バザーのみ"),$("#item_list tbody").append("<tr><th>"+e[t].name+"</th><td>"+e[t].count+"</td><td>"+n+"</td></tr>")}}$(document).ready(function(){$("#added_contents").hide(),$("#default_contents").show()});